package week2.day2;

public class EvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int data = 33;
     if(data%2==0) {
    	 System.out.println("even number");
     }else if(data%2!=0) {
    	 System.out.println("odd number");
     }
     else {
    	 System.out.println("Not even number");
     }
	}

}
