package week2.day2;

public class LearnIteration {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 2; i <=10 ; i++) {
			System.out.println(i);
		}
		
        System.out.println("----------------------");
        
        for (int i = 10; i >=1; i--) {
			System.out.println(i);
        }
	}
	}
	


