package week2.day2;

public class NegativeNumberToPostiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int num= -40;
       if(num<0) {
    	   System.out.println("It is a negative number ");
    	   num=num*-1;
    	   System.out.println(num);
       }
	}

}
