package week2.day2;

public class PrintOddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int num=45;
	    for (int i = 0; i <= num; i++) {
	    	if(i%2==1) {
	    		System.out.println("Print the  odd number: "+i);
	    		
	    	}
			
		}
	}

}
