package week2.day2;

public class TheNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int num=35;
     if(num>0) {
    	 System.out.println("The number is positive: "+num);
     }else if(num<0) {
    	 System.out.println("The number is negative: "+num);
     }else if (num==0){
    	 System.out.println("The number is neither positive nor negative: "+num);
     }
	}

}
